import React, { useEffect, useState } from "react";
import DropDown from "../../../components/dropDown/DropDown";
import LineChart from "../../../components/Graph/lineChart/LineChart";
import { getHttp } from "../../../service/APIRequest";

interface Chart2Props {
  startDate: any;
  endDate: any;
}
const Chart2: React.FC<Chart2Props> = ({ startDate, endDate }) => {
  console.log(startDate, endDate);
  const [selectList1Entity, setSelectList1Entity] = useState();
  const [selectList2Entity, setSelectList2Entity] = useState();
  const [chartData, setchartData] = useState([]);
  const [xAxis, setxAxis] = useState([]);
  const [selectedOption1, setSelectedOption1] = useState(null);
  const [selectedOption2, setSelectedOption2] = useState(null);
  const [dateRangeProps, setdateRangeProps] = useState({
    fromDate: startDate,
    toDate: endDate,
  });

  useEffect(() => {
    getEntityList();
    console.log("-===", dateRangeProps);
  }, [startDate]);

  const getEntityList = async () => {
    const response = await getHttp("entity/getAll");
    await setSelectList1Entity(response);
    await setSelectList2Entity(response);
    //setentityListForDropDown(response);
  };

  function handleChange1Value(selectedOption) {
    console.log(selectedOption);
    // setSelectedOption1(selectedOption);
    // setSelectedOption2((prevSelectedOption2) => {
    //   if (
    //     selectedOption &&
    //     selectedOption.value === prevSelectedOption2?.value
    //   ) {
    //     return null;
    //   }
    //   return prevSelectedOption2;
    // });
    drawChart(selectedOption.label);
  }

  function handleChange2Value(selectedOption) {
    console.log(selectedOption);
    drawChart(selectedOption.label);
    // setSelectedOption2(selectedOption);
    // // Disable or hide the selected option in the first dropdown
    // setSelectedOption1((prevSelectedOption1) => {
    //   if (
    //     selectedOption &&
    //     selectedOption.value === prevSelectedOption1?.value
    //   ) {
    //     return null;
    //   }
    //   return prevSelectedOption1;
    // });
  }

  async function drawChart(entityName) {
    console.log(dateRangeProps);
    const response = await getHttp("skills/averageScoreOfEntity", {
      startDate: dateRangeProps.fromDate,
      endDate: dateRangeProps.toDate,
      entityName: entityName,
    });
    console.log(response);
  }

  return (
    <div>
      <DropDown
        value={selectedOption1}
        mainList={selectList1Entity}
        name="First Entity"
        onChangeDropDownItem={handleChange1Value}
        isMulti={false}
      />
      <DropDown
        value={selectedOption2}
        mainList={selectList2Entity}
        name="Second Entity"
        onChangeDropDownItem={handleChange2Value}
        isMulti={false}
      />
      <LineChart data={chartData} xAxis={xAxis} />
    </div>
  );
};

export default Chart2;
