import React, { useState } from "react";
import { postHttp } from "../../service/APIRequest";
import "./UserCreation.css";

export const UserCreation = () => {
  const [userData, setUserData] = useState({
    name: "",
    email: "",
    role: "",
    entity: "",
    password: "",
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    const endpoint = "/auth/user/addUser";
    postHttp(endpoint, userData)
      .then((response) => {
        console.log(response);
        // if (response) {
        //   setresponseText(response);
        // }
        // setSelectedFile(null);
        // setSelectedOption(null);
        // clearFileInput();
      })
      .catch((error) => {
        console.log(error);
        // Store.dispatch(showErrorMesaage(error.error));
        // //setresponseText(error);
        // clearFileInput();
        // handle error
      });
  };

  return (
    <div className="user-creation-form">
      <h2>User Creation</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <div className="display-flex">
            <div className="align-center">Name</div>
            <input
              type="text"
              placeholder="Username"
              value={userData.name}
              onChange={(e) =>
                setUserData({ ...userData, name: e.target.value })
              }
              className="button"
            />
          </div>
          <div className="display-flex">
            <div className="align-center">Username</div>
            <input
              type="text"
              placeholder="Username"
              value={userData.name}
              onChange={(e) =>
                setUserData({ ...userData, name: e.target.value })
              }
              className="button"
            />
          </div>
          <div className="display-flex">
            <div className="align-center">Email</div>
            <input
              type="email"
              placeholder="Username"
              value={userData.email}
              onChange={(e) =>
                setUserData({ ...userData, email: e.target.value })
              }
              className="button"
              required
            />
          </div>

          <label>Name:</label>
          <input
            type="text"
            value={userData.name}
            onChange={(e) => setUserData({ ...userData, name: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            value={userData.email}
            onChange={(e) =>
              setUserData({ ...userData, email: e.target.value })
            }
            required
          />
        </div>
        <div className="form-group">
          <label>Role:</label>
          <select
            value={userData.role}
            onChange={(e) => setUserData({ ...userData, role: e.target.value })}
            required
          >
            <option value="">Select Role</option>
            <option value="admin">Admin</option>
            <option value="user">User</option>
            {/* Add more roles as needed */}
          </select>
        </div>
        <div className="form-group">
          <label>Entity:</label>
          <input
            type="text"
            value={userData.entity}
            onChange={(e) =>
              setUserData({ ...userData, entity: e.target.value })
            }
            required
          />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input
            type="password"
            value={userData.password}
            onChange={(e) =>
              setUserData({ ...userData, password: e.target.value })
            }
            required
          />
        </div>
        <button type="submit">Create User</button>
      </form>
    </div>
  );
};
