import React, { useEffect, useState } from "react";
import LineChart from "../../../components/Graph/lineChart/LineChart";
import { getHttp } from "../../../service/APIRequest";

interface Chart2props {
  data?: any;
}

const Chart2: React.FC<Chart2props> = ({ data }) => {
  const [averageSoreEntity, setaverageSoreEntity] = useState();
  const [chartData, setchartData] = useState([]);

  useEffect(() => {
    getAverageScoreOfEntity();
  }, []);

  const getAverageScoreOfEntity = async () => {
    const response = await getHttp("entity/getAverageOfEachEntities");
    await setaverageSoreEntity(response);
    // setchartData([
    //   {
    //     name: "test",
    //     data: response?.map((item) => item.averageSkill),
    //   },
    // ]);
    //var chartObject;
    const newData = [];
    for (var i = 0; i < response.length; i++) {
      var chartObject = {
        name: response[i].entityName,
        data: [response[i].averageSkill, 10],
      };
      newData.push(chartObject);
      //setchartData((prevChartData) => [...prevChartData, chartObject]);
    }
    setchartData(newData);
    //setchartData();
  };
  return (
    <div>
      <LineChart data={data} />
    </div>
  );
};

export default Chart2;
