import { SHOW_ERROR, HIDE_ERROR } from "./actionTypes";

export const incrementCounter = () => ({
  type: "INCREMENT_COUNTER",
});

export const showErrorMesaage = (errorMessage) => ({
  type: SHOW_ERROR,
  payload: {
    errorMessage,
  },
});

export const hideErrorMesaage = () => ({
  type: HIDE_ERROR,
});
