import React, { useEffect, useState } from "react";
import { getHttp } from "../../service/APIRequest";
import DateInput from "../datePicker/DateInput";

interface DateRangeFilterProps {
  FetchChartData: any;
  fetchDates?: any;
}
export const DateRangeFilter: React.FC<DateRangeFilterProps> = ({
  FetchChartData,
  fetchDates,
}) => {
  const [startDate, setStartDate] = useState<any>();
  const [endDate, setEndDate] = useState(null);
  const [endCalendarVisible, setEndCalendarVisible] = useState(true);

  useEffect(() => {
    const today = new Date();
    const defaultEndDate = today.toISOString().slice(0, 10);
    const oneYearAgo = new Date(
      today.getFullYear() - 1,
      today.getMonth(),
      today.getDate()
    );
    const defaultStartDate = oneYearAgo.toISOString().slice(0, 10);
    setTimeout(() => {
      setStartDate(defaultStartDate);
      setEndDate(defaultEndDate);
      filterData(defaultStartDate, defaultEndDate);
    }, 110);
  }, []);

  const filterData = async (startDate1?, endDate1?) => {
    console.log(startDate1, endDate1);
    fetchDates(startDate1 || startDate, endDate1 || endDate);
    if ((startDate && endDate) || (startDate1 && endDate1)) {
      const response = await getHttp("skills/companyAverageScore", {
        startDate: startDate || startDate1,
        endDate: endDate || endDate1,
      });
      FetchChartData(response);

      return { startDate, endDate };
    }
    return null;
  };

  function changeFilterStartDate(changeInStartDate) {
    setEndCalendarVisible(true);
    setStartDate(changeInStartDate);
    setEndDate(changeInStartDate);
  }

  function changeFilterEndDate(changeInEndDate) {
    setEndDate(changeInEndDate);
  }

  return (
    <div>
      <div>
        <label>Start Date:</label>
        <DateInput
          maxDate={new Date().toISOString().substr(0, 10)}
          value={startDate}
          dateChangeEvent={changeFilterStartDate}
        />
      </div>
      {endCalendarVisible && (
        <div>
          <label>End Date:</label>
          <DateInput
            maxDate={new Date().toISOString().substr(0, 10)}
            value={endDate}
            dateChangeEvent={changeFilterEndDate}
            minDate={startDate}
          />
        </div>
      )}
      <button onClick={() => filterData(startDate, endDate)}>
        Filter Data
      </button>
    </div>
  );
};
