import React, { useEffect, useState } from "react";
import { getHttp } from "../../../service/APIRequest";

interface Chart3Props {
  startDate: any;
  endDate: any;
  //data?: any;
}
export const Chart3: React.FC<Chart3Props> = ({ startDate, endDate, data }) => {
  const [chartData, setchartData] = useState(data);
  const [xAxis, setxAxis] = useState([]);
  const [dateRangeProps, setdateRangeProps] = useState({
    fromDate: startDate,
    toDate: endDate,
  });

  const [options, setOptions] = useState({
    chart: {
      id: "bar-chart",
    },
    xaxis: {
      categories: chartData?.map((company) => company.entityName),
    },
    plotOptions: {
      bar: {
        colors: {
          ranges: [
            {
              from: -100,
              to: 0,
              color: "#b70849",
            },
            {
              from: 0,
              to: 100,
              color: "#1034a6",
            },
          ],
        },
      },
    },
    dataLabels: {
      enabled: true,
      formatter: function (val, opts) {
        const isPositive = val >= 0;
        return isPositive ? `+${val}` : val.toString();
      },
    },
  });
  console.log(endDate, startDate, chartData);

  useEffect(() => {
    getScoreOfSelectedEntity(startDate, endDate);
    setchartData(data);
    setdateRangeProps({ fromDate: startDate, toDate: endDate });
    console.log(startDate, "???????????????", chartData);
  }, [startDate, endDate]);

  const getScoreOfSelectedEntity = async (startDate, endDate) => {
    const response = await getHttp("skills/skill-ranks", {
      startDate: dateRangeProps.fromDate,
      endDate: dateRangeProps.toDate,
    });
    console.log(response);
    //drawChart(response);
  };

  return <div>Chart3</div>;
};
