import React, { useState } from "react";
import { InputField } from "../InputField/InputField";

export const Personal = () => {
  const [formData, setFormData] = useState({
    employeeNumber: "",
    employeeName: "",
    role: "",
    nationality: "",
    totalYearsExp: "",
    csExperience: "",
    jobDescription: "",
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Replace 'YOUR_BACKEND_API_URL' with your actual backend API endpoint
      // const response = await axios.post("YOUR_BACKEND_API_URL", formData);

      // console.log("Form data submitted:", response.data);
      // Optionally, reset the form after successful submission
      setFormData({
        employeeNumber: "",
        employeeName: "",
        role: "",
        nationality: "",
        totalYearsExp: "",
        csExperience: "",
        jobDescription: "",
      });
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  const handleChange = (e) => {
    console.log(formData, e.target.value, e.target.name);
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Employee Number:
          <input
            type="text"
            name="employeeNumber"
            value={formData.employeeNumber}
            onChange={handleChange}
            required
          />
        </label>
        <InputField
          label="Employee Number"
          value={formData.employeeNumber}
          onChange={handleChange}
          name="employeeNumber"
        />
        {/* Other input fields (Name, Role Dropdown, Nationality Dropdown, Total Years Exp, CS Exp, Job Description) */}

        {/* <button type="submit">Next</button> */}
      </form>
    </div>
  );
};
