import React from "react";
import "./Popover.css";

interface PopoverInterface {
  tablePopupData: any;
  popoverPosition?: any;
}

export const Popover: React.FC<PopoverInterface> = ({
  tablePopupData,
  popoverPosition,
}) => {
  const popoverStyle: any = {
    position: "absolute",
    left: `${popoverPosition?.x - 125}px`,
    top: `${200}px`,
    //top: `${popoverPosition.y}px`,
    // Additional styling as needed
  };
  console.log(tablePopupData);
  return (
    <div
      className="popup"
      //style={popoverStyle ? popoverStyle : ""}
      // style={{
      //   left: popupPosition.popupX,
      //   top: popupPosition.popupY,
      // }}
    >
      {tablePopupData?.map((data, index) => (
        <p key={index}>
          {data.name}- {data.job}
        </p>
      ))}
      <div className="popup-arrow"></div>
    </div>
  );
};
