import React, { useEffect, useState } from "react";
import { getHttp } from "../../service/APIRequest";
import { allSkillsByRoleId } from "../../service/Config";

export const CompetancyEvaluation = () => {
  const [courses, setCourses] = useState([[""]]);

  const [allSkills, setallSkills] = useState<any>({});

  useEffect(() => {
    getSkillSet();
  }, []);

  const getSkillSet = () => {
    getHttp(allSkillsByRoleId + 4)
      .then((response) => {
        console.log(response);
        setallSkills(response);
      })
      .catch((error) => {
        console.log(error);
        // handle error
      });
    ///
  };

  //   const handleTrainingDetailsChange = (
  //     category,
  //     skillId,
  //     detailIndex,
  //     field,
  //     value
  //   ) => {
  //     if (category === "technical") {
  //       const updatedSkills = technicalSkills.map((skill) =>
  //         skill.id === skillId
  //           ? {
  //               ...skill,
  //               trainingDetails: skill.trainingDetails.map((detail, index) =>
  //                 index === detailIndex ? { ...detail, [field]: value } : detail
  //               ),
  //             }
  //           : skill
  //       );
  //       setTechnicalSkills(updatedSkills);
  //     } else if (category === "soft") {
  //       const updatedSkills = softSkills.map((skill) =>
  //         skill.id === skillId
  //           ? {
  //               ...skill,
  //               trainingDetails: skill.trainingDetails.map((detail, index) =>
  //                 index === detailIndex ? { ...detail, [field]: value } : detail
  //               ),
  //             }
  //           : skill
  //       );
  //       setSoftSkills(updatedSkills);
  //     }
  //   };

  const handleAddTrainingDetails = (category, skillId) => {
    if (category === "technical") {
      const updatedSkills = allSkills.technicalSkills.map((skill) =>
        skill.id === skillId
          ? {
              ...skill,
              trainingDetails: [
                ...skill.trainingDetails,
                { details: "", enrollmentDate: "", status: "" },
              ],
            }
          : skill
      );
      setallSkills({ ...allSkills, updatedSkills });
    } else if (category === "soft") {
      const updatedSkills = allSkills.softSkills.map((skill) =>
        skill.id === skillId
          ? {
              ...skill,
              trainingDetails: [
                ...skill.trainingDetails,
                { details: "", enrollmentDate: "", status: "" },
              ],
            }
          : skill
      );
      setallSkills({ ...allSkills, updatedSkills });
    }
  };

  const handleAddCourse = (index) => {
    const updatedCourses = [...courses];
    updatedCourses[index].push("");
    setCourses(updatedCourses);
  };

  const handleCourseChange = (rowIndex, colIndex, value) => {
    const updatedCourses = [...courses];
    updatedCourses[rowIndex][colIndex] = value;
    setCourses(updatedCourses);
  };
  return (
    <div>
      {allSkills?.technicalSkills?.map((skill, skillIndex) => (
        <div key={skillIndex}>
          <input type="checkbox" />
          {/* <label>{skill}</label> */}
          {courses[skillIndex].map((course, courseIndex) => (
            <div key={courseIndex}>
              <input
                type="text"
                value={course}
                onChange={(e) =>
                  handleCourseChange(skillIndex, courseIndex, e.target.value)
                }
                placeholder="Course"
              />
              {courseIndex === courses[skillIndex].length - 1 && (
                <button onClick={() => handleAddCourse(skillIndex)}>+</button>
              )}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};
