import React, { useEffect, useState } from "react";
//import { useNavigate } from "react-router-dom";

import EntityBox from "../../components/entityBox/EntityBox";
import FilterSection from "../../components/filterSection/FilterSection";
import { getHttp } from "../../service/APIRequest";
import { mainEntity, skillMaximumScoreValue } from "../../service/Constants";

export const EntityList = (props) => {
  const [updatedEmployeeDetails, setupdatedEmployeeDetails] = useState<any>();
  const [updatedEntityList, setupdatedEntityList] = useState<any>(mainEntity);

  useEffect(() => {
    getEntityList();
    getAllEmployeeWithskills();
  }, []);

  useEffect(() => {
    getOverAllScore();
  }, [updatedEmployeeDetails]);

  const getEntityList = async () => {
    const response = await getHttp("allEmployeesWithSkills");
    await setupdatedEmployeeDetails(response);
  };

  const changeDisplaySetData = (data) => {
    setupdatedEntityList(data);
  };

  const getAllEmployeeWithskills = async () => {
    const response = await getHttp("allEmployeesWithSkills");
    await setupdatedEmployeeDetails(response);
  };

  const getOverAllScore = () => {
    updatedEntityList.map((entity, index) => {
      var achievedEntityScore = 0;
      var achievedTotalScore = 0;
      updatedEmployeeDetails?.map((employee, number) => {
        var achievedEmployeeScore = 0;
        var employeeTotalScore = 0;

        if (entity.label.toLowerCase() === employee.entity.toLowerCase()) {
          employee.skills.map((item, id) => {
            achievedEmployeeScore = achievedEmployeeScore + item.score;
            employeeTotalScore = employeeTotalScore + skillMaximumScoreValue;
          });

          updatedEmployeeDetails[number].employeeScorePercentage =
            (achievedEmployeeScore / employeeTotalScore) * 100;

          achievedEntityScore = achievedEntityScore + achievedEmployeeScore;
          achievedTotalScore = achievedTotalScore + employeeTotalScore;
        } else {
          //updatedEmployeeDetails[number].employeeScorePercentage = 0;
        }
      });
      setupdatedEmployeeDetails(updatedEmployeeDetails);
      updatedEntityList[index].entityTotalSkillPercentage =
        (achievedEntityScore / achievedTotalScore) * 100;
    });
    setupdatedEntityList(updatedEntityList);
  };

  const goToEntityDetails = (entityName) => {
    props.onEntityClick(entityName);
    //history.push("/tab2/entity-details");
  };
  return (
    <div>
      <FilterSection onSelectedValueChange={changeDisplaySetData} />
      {/* <MultiSelectDropdown /> */}

      <div className="entity-container">
        {updatedEntityList.map((item, index) => (
          <div
            key={index}
            className="each-entity-container"
            onClick={() => goToEntityDetails(item.label)}
          >
            <EntityBox
              logo={updatedEntityList[index].logo}
              name={updatedEntityList[index].label}
              totalEntityScore={Math.round(item.entityTotalSkillPercentage)}
            />
          </div>
        ))}
      </div>
    </div>
  );
};
