const data = {
  datasets: [
    {
      data: [10, 20, 30],
      backgroundColor: ["red", "blue", "green"],
      label: "Layer 1",
    },
    {
      data: [5, 15, 25],
      backgroundColor: ["orange", "purple", "yellow"],
      label: "Layer 2",
    },
    {
      data: [2, 8, 12],
      backgroundColor: ["pink", "gray", "brown"],
      label: "Layer 3",
    },
  ],
};

const options1 = {
  cutoutPercentage: [70, 50, 30],
  legend: {
    display: true,
    position: "top",
    labels: [
      { text: "Red", fillStyle: "red" },
      { text: "Blue", fillStyle: "blue" },
      { text: "Green", fillStyle: "green" },
      { text: "Orange", fillStyle: "orange" },
      { text: "Purple", fillStyle: "purple" },
      { text: "Yellow", fillStyle: "yellow" },
      { text: "Pink", fillStyle: "pink" },
      { text: "Gray", fillStyle: "gray" },
      { text: "Brown", fillStyle: "brown" },
    ],
  },
};

const options = {
  plugins: {
    datalabels: {
      font: {
        size: 14,
      },
      legend: {
        position: "top",
        labels: {
          usePointStyle: true,
          pointStyle: "circle",
        },
      },
    },
  },
};

const MyChart = () => {
  return <Doughnut data={data} options={options} />;
};

export default MyChart;
