import React, { useEffect, useState } from "react";
import Doughnut from "../../components/Graph/donutChart/Doughnut";
//import Doughnut from "../Graph/donutChart/Doughnut";

import { getHttp } from "../../service/APIRequest";

const CategoryList = ({ categoryClick }) => {
  const [mainCategoryListByCount, setMainCategoryListByCount] = useState<any>(
    []
  );
  const [catLevelDetails, setCatLevelDetails] = useState([]);

  useEffect(() => {
    fetchCatagoryList();
    fetchCategoryLevelDetails();
  }, []);

  const fetchCatagoryList = async (param?) => {
    const response = await getHttp("count-by-category");
    setMainCategoryListByCount(response);
  };

  // const fetchCatagoryLvelDetails = (param?) => {
  //   for (var i = 1; i <= 3; i++) {
  //     getHttp("categories/" + i + "/subcategories")
  //       .then((response) => {
  //         console.log(response);
  //         var catName;
  //         if ((i = 1)) {
  //           catName = "Data analytics";
  //         } else if ((i = 2)) {
  //           catName = "ICT";
  //         } else {
  //           catName = "Cyber security";
  //         }
  //         const newData = [
  //           {
  //             categoryname: catName,
  //             details: response.map((item) => {
  //               return {
  //                 id: item.id,
  //                 category: item.category,
  //                 levelCounts: item.levelCounts,
  //                 count: item.count,
  //                 grantTotalEmp: item.grantTotalEmp,
  //               };
  //             }),
  //           },
  //         ];

  //         setCatLevelDetails([...catLevelDetails, newData]); // handle success
  //         console.log(newData);
  //       })
  //       .catch((error) => {
  //         console.log(error);
  //         // handle error
  //       });
  //   }
  // };

  const fetchCategoryLevelDetails = async (param?) => {
    const newCatLevelDetails = [];

    for (let i = 1; i <= 3; i++) {
      try {
        const response = await getHttp(`categories/${i}/subcategories`);

        let catName;
        if (i === 1) {
          catName = "Data analytics";
        } else if (i === 2) {
          catName = "ICT";
        } else {
          catName = "Cyber security";
        }

        const newData = {
          categoryname: catName,
          details: response.map((item) => ({
            id: item.id,
            category: item.category,
            levelCounts: item.levelCounts,
            count: item.count,
            grantTotalEmp: item.grantTotalEmp,
          })),
        };

        newCatLevelDetails.push(newData);
      } catch (error) {
        console.log(error);
        // handle error
      }
    }

    setCatLevelDetails(newCatLevelDetails); // handle success
    console.log(catLevelDetails, newCatLevelDetails);
  };

  const goToCategoryDetailsPage = (id, item?) => {
    categoryClick(id, item.category);
  };

  return (
    <div className="main-container">
      <div className="main-heading">Dubai Digital Skills</div>
      <div className="seperate-employee-counter">
        {mainCategoryListByCount?.map((item, index) => (
          <div
            className="each-counter"
            key={index}
            onClick={() => goToCategoryDetailsPage(item.id, item)}
          >
            <div>{item.category}</div>
            <div className="counter-details">
              <div># of employees </div>
              <div className="count"> {item?.count}</div>
            </div>
          </div>
        ))}
      </div>
      <div className="content">
        <Doughnut
          details={mainCategoryListByCount}
          heading="Dubai Skills %"
          showLegend={true}
        />
      </div>
    </div>
  );
};

export default CategoryList;
