import React, { useEffect, useState } from "react";

import Chart from "react-apexcharts";

const LineChart = ({ data, xAxis }) => {
  const [dataSeries, setdataSeries] = useState(data);
  const [axisData, setaxisData] = useState(xAxis);
  console.log(data, xAxis);

  useEffect(() => {
    setdataSeries(data);
    setaxisData(xAxis);
  });

  //const series = data;
  //data?.map((item) => {name:item.companyName,data:item.map(element)=>})

  // [
  //   { name: "Estimate", data: [10, 15, 2] },
  //   { name: "Actual", data: [12, 10, 20] },
  // ];
  const series = [
    { name: "Dubai Economy and Tourism", data: [1.8, 2.1, 1.7] },
    { name: "dewa", data: [1.9, 2.2, 2.1] },
    { name: "dubai chamber", data: [2.1, 2.6, 2.1] },
  ];
  //const series = data?.map((item) => item.category);

  const options = {
    xaxis: {
      categories: xAxis,
    },
    yaxis: {
      forceNiceScale: true,
    },
  };

  return (
    <div>
      <Chart options={options} series={series} type="line" width={400} />
    </div>
  );
};

export default LineChart;
