import React, { useEffect, useState } from "react";
import { getHttp } from "../../service/APIRequest";
import { userapi } from "../../service/Config";

export const UserList = ({ refresh }) => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    getAllUsers();
  }, [refresh]);

  const getAllUsers = () => {
    getHttp(userapi + "allUsers")
      .then((response) => {
        console.log(response);
        setUsers(response.data);
      })
      .catch((error) => {
        console.log(error);
        // handle error
      });
    ///
  };

  return (
    <div className="user-list-container">
      <h2>User List</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Entity</th>
            {/* Other table headers */}
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>{user.username}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>{user.entity}</td>
              {/* Other table cells */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
