import React from "react";
import { Bar } from "react-chartjs-2";

const data = {
  labels: ["January", "February", "March", "April", "May"],
  datasets: [
    {
      label: "Layer 1",
      data: [10, 20, 30, 40, 50],
      backgroundColor: "rgba(255, 99, 132, 0.2)",
      borderColor: "rgba(255, 99, 132, 1)",
      borderWidth: 1,
      datalabels: {
        display: false,
      },
      innerLabelColors: [
        "rgba(255, 99, 132, 1)",
        "rgba(54, 162, 235, 1)",
        "rgba(255, 206, 86, 1)",
        "rgba(75, 192, 192, 1)",
        "rgba(153, 102, 255, 1)",
      ],
    },
    {
      label: "Layer 2",
      data: [20, 30, 40, 50, 60],
      backgroundColor: "rgba(54, 162, 235, 0.2)",
      borderColor: "rgba(54, 162, 235, 1)",
      borderWidth: 1,
      datalabels: {
        display: false,
      },
      innerLabelColors: [
        "rgba(255, 99, 132, 1)",
        "rgba(54, 162, 235, 1)",
        "rgba(255, 206, 86, 1)",
        "rgba(75, 192, 192, 1)",
        "rgba(153, 102, 255, 1)",
      ],
    },
  ],
};

const options = {
  plugins: {
    datalabels: {
      // anchor: "end",
      //align: "end",
      color: "white",
      font: {
        size: 14,
      },
      formatter: (value, ctx) => {
        let label = "";
        if (ctx.datasetIndex === 0) {
          label = "Layer 1:";
        } else if (ctx.datasetIndex === 1) {
          label = "Layer 2:";
        }
        const innerLabelColors = ctx.dataset.innerLabelColors;
        const innerLabelColor = innerLabelColors[ctx.dataIndex];
        return {
          text: label + value,
          color: innerLabelColor,
        };
      },
    },
  },
};

const MyChart = () => {
  return <Bar data={data} options={options} />;
};

export default MyChart;
