import React, { useState } from "react";
import DropDown from "../../components/dropDown/DropDown";
import ErrorComponent from "../../components/errorComponent/ErrorComponent";
import Popup from "../../components/popup/Popup";
import { UserList } from "../../components/userList/UserList";
import { showErrorMesaage } from "../../redux/features/errorSlice";
import Store from "../../redux/Store";
import { getHttp, postHttp } from "../../service/APIRequest";
import { userapi } from "../../service/Config";
import { userTypes } from "../../service/Constants";
import "./UserCreation.css";

export const UserCreation = () => {
  const [userData, setUserData] = useState({
    id: 0,
    name: "",
    username: "",
    email: "",
    role: "main_admin",
    entity: "",
    password: "",
  });

  const [entityList, setEntityList] = useState<any>();
  const [flag, setflag] = useState(true);
  const [userType, setUserType] = useState("admin");
  const [createUser, setcreateUser] = useState(true);
  const [openPopup, setopenPopup] = useState(true);

  const handleSubmit = async (e) => {
    console.log(userData);
    e.preventDefault();
    var endpoint;
    if (createUser) {
      endpoint = userapi + "addUser";
    } else {
      endpoint = userapi + 1;
    }
    postHttp(endpoint, userData)
      .then((response) => {
        console.log(response);

        if (response.status >= 200 && response.status < 300) {
          setUserData({
            name: "",
            username: "",
            email: "",
            role: "",
            entity: "",
            password: "",
          });
          Store.dispatch(showErrorMesaage("User created successfully"));
          setflag(!flag);
        }
      })
      .catch((error) => {
        console.log(error);
        // Store.dispatch(showErrorMesaage(error.error));
        // //setresponseText(error);
        // clearFileInput();
        // handle error
      });
  };

  const handleNameChange = (event) => {
    const inputValue = event.target.value;
    const formattedName = inputValue.toLowerCase().replace(/\s+/g, ".");
    console.log(formattedName);
    setUserData({
      ...userData,
      name: event.target.value,
      username: formattedName,
    });
    //setName(inputValue);

    //setUsername(formattedName);
  };

  const handleUserTypeChange = (value) => {
    setUserType(value);
    setUserData({ ...userData, role: value });
    if (value == "user") {
      getEntityList();
    }
  };

  const getEntityList = async () => {
    const response = await getHttp("entity/getNameDetails"); //
    await setEntityList(response);
  };

  const setUserDetailsForEdit = (details) => {
    console.log(details);
    setcreateUser(false);
    handleUserTypeChange(details.role);
    setUserData(details);
    window.scrollTo({
      top: 0,
      behavior: "smooth", // Optional: Adds smooth scrolling behavior
    });
  };

  return (
    <div className="create-user-container">
      <Popup
        isOpen={openPopup}
        onRequestClose={() => setopenPopup(false)}
        onConfirm={undefined}
        bodyContent={<p>This is the body content of the popup.</p>}
      />
      <div className="user-creation-form login-form1">
        {createUser ? <h2>User Creation</h2> : <h2>Edit User</h2>}
        <form onSubmit={handleSubmit} className="create-container">
          <div className="form-group">
            <div className="display-flex">
              <div className="align-center">Name</div>
              <input
                type="text"
                placeholder="Name"
                value={userData.name}
                onChange={(e) =>
                  //setUserData({ ...userData, name: e.target.value })
                  handleNameChange(e)
                }
                className="button"
                required
              />
            </div>
            <div className="display-flex">
              <div className="align-center">Username</div>
              <input
                type="text"
                placeholder="USERNAME"
                value={userData.username}
                //   style={{ textTransform: "lowercase" }}
                // onChange={(e) =>
                //setUserData({ ...userData, username: e.target.value })
                // }
                readOnly
                className="button lowercase"
              />
            </div>
            <div className="display-flex">
              <div className="align-center">Email</div>
              <input
                type="email"
                placeholder="Email"
                value={userData.email}
                onChange={(e) =>
                  setUserData({ ...userData, email: e.target.value })
                }
                className="button"
                required
              />
            </div>
            <div className="display-flex">
              <div className="align-center">Role</div>

              <select
                className="new-user button"
                value={userType}
                onChange={(event) => handleUserTypeChange(event.target.value)}
              >
                {userTypes.map((item, index) => (
                  <option key={index} value={item.label}>
                    {item.name}
                  </option>
                ))}
              </select>
            </div>
            {userType === "user" && (
              <div className="display-flex">
                <div className="align-center">Entity</div>
                <select
                  className="new-user button"
                  value={userData.entity}
                  onChange={(e) =>
                    setUserData({ ...userData, entity: e.target.value })
                  }
                >
                  {entityList?.map((item, index) => (
                    <option key={index} value={item.name}>
                      {item.label}
                    </option>
                  ))}
                </select>
              </div>
            )}

            {createUser && (
              <div className="display-flex">
                <div className="align-center">password</div>
                <input
                  type="password"
                  placeholder="password"
                  value={userData.password}
                  onChange={(e) =>
                    setUserData({ ...userData, password: e.target.value })
                  }
                  className="button"
                  required
                />
              </div>
            )}
          </div>
          {createUser ? (
            <button type="submit">Create User</button>
          ) : (
            <button type="submit">Update User</button>
          )}

          <ErrorComponent />
        </form>
      </div>
      <UserList refresh={flag} setUserDetailsForEdit={setUserDetailsForEdit} />
    </div>
  );
};
