import React, { useEffect, useState } from "react";
import { BarChart } from "../../../components/Graph/barChart/BarChart";
import Component1 from "../../../components/test/Component1";
import { getHttp } from "../../../service/APIRequest";

interface Chart3Props {
  startDate: any;
  endDate: any;
  selectedEntityList?: any;
  entityList?: any;
  fetchChart3Data?: any;
}
export const Chart3: React.FC<Chart3Props> = ({
  startDate,
  endDate,
  selectedEntityList,
  entityList,
  fetchChart3Data,
}) => {
  const [chartData, setchartData] = useState<any>();
  const [xAxis, setxAxis] = useState([]);
  const [dateRangeProps, setdateRangeProps] = useState({
    fromDate: startDate,
    toDate: endDate,
  });
  const [selectedEntities, setselectedEntities] = useState(selectedEntityList);
  const [allEntityList, setallEntityList] = useState(entityList);

  useEffect(() => {
    setallEntityList(allEntityList);
    setselectedEntities(selectedEntityList);
    getScoreOfSelectedEntity(startDate, endDate, selectedEntityList);
  }, [selectedEntityList]);

  useEffect(() => {
    getScoreOfSelectedEntity(startDate, endDate);
  }, []);

  useEffect(() => {
    //getScoreOfSelectedEntity(startDate, endDate);
    //setchartData(data);
    setdateRangeProps({ fromDate: startDate, toDate: endDate });
  }, []);

  const getScoreOfSelectedEntity = async (
    startDate,
    endDate,
    selectedEntities?
  ) => {
    var entityLists = selectedEntities;
    if (entityLists?.length == 0) {
      entityLists = entityList?.map((item) => item.label);
    }
    const response = await getHttp("skills/skill-ranks", {
      startDate: dateRangeProps.fromDate || startDate,
      endDate: dateRangeProps.toDate || endDate,
      entityNames: entityLists.join(","),
    });
    await drawChart(response);
    fetchChart3Data(response);
  };

  async function drawChart(responseData?) {
    // var dataOfChart = [
    //   {
    //     name: "Improvement over last year", //chartData?.map((company) => company.entityName),
    //     data: responseData?.map((company) => company.avg_score_diff),
    //   },
    // ];
    var xAxisdata = responseData?.map((company) => company.entityName);
    setxAxis(xAxisdata);
    setchartData(responseData?.map((company) => company.avg_score_diff));
  }

  return (
    <div>
      <BarChart
        data={chartData}
        xAxis={xAxis}
        title={"Improvement over last year"}
        type="progress"
      />
    </div>
  );
};
