import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const DateInput = () => {
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(null);

  const handleStartChange = (date) => {
    setStartDate(date);
    setEndDate(null);
  };

  const handleEndChange = (date) => {
    setEndDate(date);
  };

  const filterData = () => {
    if (startDate && endDate) {
      const filteredData = data.filter(
        (item) =>
          new Date(item.date) >= new Date(startDate) &&
          new Date(item.date) <= new Date(endDate)
      );
      console.log(filteredData);
    }
  };

  const data = [
    { id: 1, date: "2022-01-01" },
    { id: 2, date: "2022-02-05" },
    { id: 3, date: "2022-03-15" },
    { id: 4, date: "2023-04-02" },
    { id: 5, date: "2023-05-20" },
  ];

  const monthNames = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  const startMonth = startDate.getMonth();
  const endMonth = endDate ? endDate.getMonth() : null;

  const renderCustomHeader = ({ date, decreaseMonth, increaseMonth }) => {
    const month = monthNames[date.getMonth()];
    const year = date.getFullYear();
    return (
      <div className="header">
        <button className="prev" onClick={decreaseMonth}>
          {"<"}
        </button>
        <div className="current">
          {month} {year}
        </div>
        <button className="next" onClick={increaseMonth}>
          {">"}
        </button>
      </div>
    );
  };

  const renderCustomMonth = ({ month, onMonthSelect, onYearSelect }) => {
    return (
      <div className="react-datepicker__month-dropdown">
        <select
          className="react-datepicker__month-dropdown"
          value={month}
          onChange={(e) => onMonthSelect(monthNames.indexOf(e.target.value))}
        >
          {monthNames.map((monthName, index) => (
            <option key={index} value={monthName}>
              {monthName}
            </option>
          ))}
        </select>
      </div>
    );
  };

  const disabledMonths = [];
  for (let i = 0; i < startMonth; i++) {
    disabledMonths.push(i);
  }

  return (
    <div>
      <div>
        <label>Start Date:</label>
        <DatePicker
          selected={startDate}
          onChange={handleStartChange}
          showMonthYearPicker
          renderCustomHeader={renderCustomHeader}
          dateFormat="MM/yyyy"
          dropdownMode="select"
        />
      </div>
      <div>
        <label>End Date:</label>
        <DatePicker
          selected={endDate}
          onChange={handleEndChange}
          showMonthYearPicker
          renderCustomHeader={renderCustomHeader}
          dateFormat="MM/yyyy"
          dropdownMode="select"
          disabledMonths={disabledMonths}
          renderCustomDropdown={renderCustomMonth}
          minDate={startDate}
        />
      </div>
    </div>
  );
};

export default DateInput;
