import axios from "axios";
import React, { useState } from "react";
import { postHttp } from "../../service/APIRequest";
import "./FileUpload.css";

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [responseText, setresponseText] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleResponseText = () => {
    setresponseText(null);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("file", file);

    const response = postHttp("upload", formData)
      .then((response) => {
        console.log(response);
        setresponseText(response);
        // handle success
      })
      .catch((error) => {
        console.log(error);
        setresponseText(error);
        // handle error
      });

    // axios
    //   .post("http://localhost:8080/upload", formData, {
    //     headers: {
    //       "Content-Type": "multipart/form-data",
    //     },
    //   })
    //   .then((response) => {
    //     console.log(response);
    //     setresponseText(response.data);
    //     // handle success
    //   })
    //   .catch((error) => {
    //     console.log(error);
    //     // handle error
    //   });
  };
  return (
    <div className="upload-backgroound">
      <div className="upload-container">
        <form onSubmit={handleSubmit}>
          <input
            type="file"
            onChange={handleFileChange}
            onClick={handleResponseText}
          />
          <button type="submit">Upload</button>
        </form>
      </div>
      <div className="response-text">{responseText}</div>
    </div>
  );
};

export default FileUpload;
