import React, { useEffect, useState } from "react";
import LineChart from "../../../components/Graph/lineChart/LineChart";
import { getHttp } from "../../../service/APIRequest";

interface Chart1props {
  data?: any;
}

const Chart1: React.FC<Chart1props> = ({ data }) => {
  const [xAaxis, setXAxis] = useState([]);
  const [chartData, setchartData] = useState(data);

  useEffect(() => {
    setTimeout(() => {
      //setchartData(data);
      const series = data?.map((company) => {
        return {
          name: company.companyName,
          data: company.monthlyAverages?.map((monthlyAverage) => {
            return monthlyAverage.averageScore;
          }),
        };
      });
      setchartData(series);
      const xAxis = data?.map((company) => {
        console.log("eeanna", company.monthlyAverages.length);
        return company.monthlyAverages?.map((monthlyAverage) => {
          if (company.monthlyAverages.length == 1) {
            //
            console.log("eeannasssss", monthlyAverage.month);
            return monthlyAverage.month;
          } else {
            return monthlyAverage.month;
          }
        });
        //return month;
      });
      console.log(xAxis);
      if (xAaxis[xAxis.length - 1].length == 1) {
        var xaxisnew = xAxis[xAxis.length - 1].add(0, "Mar 2022");
        setXAxis(xaxisnew); // temporary
      } else {
        setXAxis(xAxis[xAxis.length - 1]);
      }
      //setXAxis(["02/2023", "05/2022", "12/2022"]);
    }, 11);
    // getAverageScoreOfEntity();
  }, [data]);

  return (
    <div>
      <LineChart data={chartData} xAxis={xAaxis} title="All entities" />
    </div>
  );
};

export default Chart1;
