import React, { useState } from "react";
import DropDown from "../../components/dropDown/DropDown";
import ErrorComponent from "../../components/errorComponent/ErrorComponent";
import { UserList } from "../../components/userList/UserList";
import { showErrorMesaage } from "../../redux/features/errorSlice";
import Store from "../../redux/Store";
import { getHttp, postHttp } from "../../service/APIRequest";
import { userapi } from "../../service/Config";
import { userTypes } from "../../service/Constants";
import "./UserCreation.css";

export const UserCreation = () => {
  const [userData, setUserData] = useState({
    name: "",
    username: "",
    email: "",
    role: "",
    entity: "",
    password: "",
  });

  const [entityList, setEntityList] = useState<any>();

  const [userType, setUserType] = useState("admin");
  const handleSubmit = async (e) => {
    e.preventDefault();
    const endpoint = userapi + "addUser";
    postHttp(endpoint, userData)
      .then((response) => {
        console.log(response);
        setUserData({
          name: "",
          username: "",
          email: "",
          role: "",
          entity: "",
          password: "",
        });
        Store.dispatch(showErrorMesaage("User created successfuly"));
      })
      .catch((error) => {
        console.log(error);
        // Store.dispatch(showErrorMesaage(error.error));
        // //setresponseText(error);
        // clearFileInput();
        // handle error
      });
  };

  const handleNameChange = (event) => {
    const inputValue = event.target.value;
    const formattedName = inputValue.toLowerCase().replace(/\s+/g, ".");
    console.log(formattedName);
    setUserData({
      ...userData,
      name: event.target.value,
      username: formattedName,
    });
    //setName(inputValue);

    //setUsername(formattedName);
  };

  const handleUserTypeChange = (event) => {
    setUserType(event.target.value);
    setUserData({ ...userData, role: event.target.value });
    if (event.target.value == "user") {
      getEntityList();
    }
  };

  const getEntityList = async () => {
    const response = await getHttp("entity/getNameDetails"); //
    await setEntityList(response);
  };

  return (
    <div className="create-user-container">
      <div className="user-creation-form">
        <h2>User Creation</h2>
        <form onSubmit={handleSubmit} className="create-container">
          <div className="form-group">
            <div className="display-flex">
              <div className="align-center">Name</div>
              <input
                type="text"
                placeholder="Name"
                value={userData.name}
                onChange={(e) =>
                  //setUserData({ ...userData, name: e.target.value })
                  handleNameChange(e)
                }
                className="button"
                required
              />
            </div>
            <div className="display-flex">
              <div className="align-center">Username</div>
              <input
                type="text"
                placeholder="USERNAME"
                value={userData.username}
                //   style={{ textTransform: "lowercase" }}
                // onChange={(e) =>
                //setUserData({ ...userData, username: e.target.value })
                // }
                readOnly
                className="button lowercase"
              />
            </div>
            <div className="display-flex">
              <div className="align-center">Email</div>
              <input
                type="email"
                placeholder="Email"
                value={userData.email}
                onChange={(e) =>
                  setUserData({ ...userData, email: e.target.value })
                }
                className="button"
                required
              />
            </div>
            <div className="display-flex">
              <div className="align-center">Role</div>
              {/* <DropDown
              mainList={entityListForDropDown}
              name="Entity"
              onChangeDropDownItem={handleChangedRoleValue}
              isMulti={true}
              classname="margin"//
            /> */}
              <select
                className="new-user button"
                value={userType}
                onChange={handleUserTypeChange}
              >
                {userTypes.map((item, index) => (
                  <option key={index} value={item.label}>
                    {item.name}
                  </option>
                ))}
              </select>
            </div>
            {userType === "user" && (
              <div className="display-flex">
                <div className="align-center">Entity</div>
                <select
                  className="new-user button"
                  value={userData.entity}
                  onChange={(e) =>
                    setUserData({ ...userData, entity: e.target.value })
                  }
                >
                  {entityList?.map((item, index) => (
                    <option key={index} value={item.name}>
                      {item.label}
                    </option>
                  ))}
                </select>
                {/* <DropDown
              mainList={entityList}
              //name="Filter"
              onChangeDropDownItem={handleEntityChange}
              isMulti={false}
            /> */}
              </div>
            )}

            <div className="display-flex">
              <div className="align-center">password</div>
              <input
                type="password"
                placeholder="password"
                value={userData.password}
                onChange={(e) =>
                  setUserData({ ...userData, password: e.target.value })
                }
                className="button"
                required
              />
            </div>
          </div>
          <button type="submit">Create User</button>
          <ErrorComponent />
        </form>
      </div>
      <UserList />
    </div>
  );
};
