import React, { useEffect, useState } from "react";
import Chart from "react-apexcharts";

interface donutChartInterface {
  details?: any;
  heading?: String;
}

const Doughnut: React.FC<donutChartInterface> = ({ details, heading }) => {
  console.log(details?.map((item) => item.count));
  // const [dataSeries, setdataSeries] = useState([
  //   { name: "# of employees in ", data: [10, 45, 78, 32] },
  // ]);

  const [dataSeries, setdataSeries] = useState(
    //[10, 45, 78, 32]
    details?.map((item) => item.count)
  );
  const [option, setOption] = useState<any>({
    legend: {
      show: false,
    },
    //labels: details?.map((item) => item.category),
    tooltip: {
      y: {
        formatter: function (val) {
          return val + ".00" + " Rs";
        },
        title: {
          formatter: function (seriesName) {
            return "# of Employees in ";
          },
        },
      },
    },
    title: {
      text: "Dubai Skills %",
      style: {
        fontSize: "14px",
        fontWeight: "bold",
        fontFamily: "Bukra",
        color: "red",
      },
    },
  });

  useEffect(() => {
    setdataSeries(details?.map((item) => item.count));
  }, [details]);

  return (
    <div>
      <div
        className="donut-heading"
        hidden={details?.length <= 0}
        //className={multiLayer ? "center-heading" : ""}
      >
        {heading}
      </div>
      <Chart options={option} series={dataSeries} type="donut" width={400} />
    </div>
  );
};

export default Doughnut;
