import React, { useEffect, useRef, useState } from "react";
import axios from "axios";

import "./Login.css";
import { getHttp, postHttp } from "../../service/APIRequest";
import { useHistory } from "react-router-dom";
import ErrorComponent from "../../components/errorComponent/ErrorComponent";
import { getUserApi, loginApi } from "../../service/Config";
import Store from "../../redux/Store";
import { createUserDetails } from "../../redux/features/userDetailsSlice";

function Login() {
  const history = useHistory();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [userType, setUserType] = useState("admin"); // Default value
  const [selectedEntity, setSelectedEntity] = useState(""); // Default value
  const [entityList, setEntityList] = useState<any>();
  const [errorMessage, setErrorMessage] = useState("");
  const [adminType, setAdminType] = useState("main_admin");

  const textBoxRef = useRef(null);

  useEffect(() => {
    // getEntityList();
    if (textBoxRef.current) {
      textBoxRef.current.focus();
    }
  }, []);

  const handleLogin = async () => {
    if (!username || !password) {
      // Display an error message for empty fields
      setErrorMessage("Username and password are required.");
      return;
    }

    setErrorMessage("");
    try {
      const response = await postHttp(loginApi, {
        username,
        password,
      }).then();
      console.log(response);

      if (response) {
        console.log(response);
        if (response?.status >= 200 && response?.status < 300) {
          getUserDetails(username);
          // sessionStorage.setItem("userDetails", JSON.stringify(response.data));
          // sessionStorage.setItem("loginStatus", "true");
          // history.push("/", response.data);
        } else if (response?.status == 403) {
          console.log("-------");
          setErrorMessage("jsdk");
        }
      }
      // }
    } catch (error) {
      console.error("Login failed", error);
      setErrorMessage(error);
    }
  };

  const getUserDetails = async (user) => {
    const userResponse = await getHttp(getUserApi + user);
    if (userResponse) {
      sessionStorage.setItem("userDetails", JSON.stringify(userResponse.data));
      Store.dispatch(createUserDetails(JSON.stringify(userResponse.data)));
      sessionStorage.setItem("loginStatus", "true");
      history.push("/", userResponse.data);
    }
  };

  // const handleUserTypeChange = (event) => {
  //   setUserType(event.target.value);
  //   if (event.target.value == "user") {
  //     getEntityList();
  //   }
  // };

  const handleEntityChange = (event) => {
    setSelectedEntity(event.target.value);
    //console.log(selectedValue);
    //setSelectedEntity(selectedValue.label);
  };

  // const getEntityList = async () => {
  //   const response = await getHttp("entity/getNameDetails"); //
  //   await setEntityList(response);
  // };

  const handleAdminTypeChange = (event) => {
    setAdminType(event.target.value);
    //setSelectedEntity(selectedValue.label);
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <div className="display-flex">
          <div className="align-center">Username</div>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="button"
            ref={textBoxRef}
          />
        </div>
        <div className="display-flex">
          <div className="align-center">Password</div>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="button"
          />
        </div>

        {/* <div className="display-flex">
          <div className="align-center">User Type</div>


          <select
            value={userType}
            onChange={handleUserTypeChange}
            className="new-user button"
          >
            {userTypes.map((item, index) => (
              <option key={index} value={item.label}>
                {item.name}
              </option>
            ))}
          </select>
        </div> */}
        {/* {userType === "admin" && (
          <div className="display-flex">
            <div className="align-center">Admin Type</div>
            <select value={adminType} onChange={handleAdminTypeChange}>
              <option value="main_admin">Main Admin</option>
              <option value="desc_admin">Desc Admin</option>
            </select>
          </div>
        )} */}

        {userType === "user" && (
          <div className="display-flex">
            <div className="align-center">Entity</div>
            <select
              value={selectedEntity}
              onChange={handleEntityChange}
              className="new-user button"
            >
              {entityList?.map((item, index) => (
                <option key={index} value={item.name}>
                  {item.label}
                </option>
              ))}
            </select>
            {/* <DropDown
              mainList={entityList}
              //name="Filter"
              onChangeDropDownItem={handleEntityChange}
              isMulti={false}
            /> */}
          </div>
        )}

        <button className="login-button" onClick={handleLogin}>
          Login
        </button>
        {errorMessage && <div className="error-message">{errorMessage}</div>}
        <ErrorComponent />
      </div>
    </div>
    // <UserLogin />
  );
}

export default Login;
