import React, { useEffect, useState } from "react";
import Chart from "react-apexcharts";
import { getHttp } from "../../../service/APIRequest";
import "../Chart.css";
import "./BarChartPopup.css";

interface BarChartInterface {
  data;
  xAxis;
  title;
  name?;
  width?: any;
  subTitle?: String;
}

export const BarChart: React.FC<BarChartInterface> = ({
  data,
  xAxis,
  title,
  name,
  width,
  subTitle,
}) => {
  const [dataSeries, setdataSeries] = useState<any>([
    {
      name: title, //will be displayed on the y-axis
      data: data,
    },
  ]);
  const [popupPosition, setPopupPosition] = useState(null);
  const [popupData, setPopupData] = useState(null);
  const [option, setOption] = useState<any>({
    legend: {
      show: false,
    },
    chart: {
      events: {
        dataPointSelection: function (event, chartContext, config) {
          const dataPointIndex = config.dataPointIndex;
          const chartContainer = document.querySelector(".bar-chart");

          const containerRect = chartContainer.getBoundingClientRect();

          const selectedXValue = option.xaxis.categories[dataPointIndex];
          const barElement = chartContext.globals.series.bars[dataPointIndex];

          const barCenterX =
            barElement.x.baseVal.value + barElement.width.baseVal.value / 2;
          const barY = barElement.y.baseVal.value;
          const popupX = barCenterX + containerRect.left;
          const popupY = barY + containerRect.top;
          eachBarClick(
            event,
            chartContext,
            config,
            selectedXValue,
            popupX,
            popupY
          );
        },
      },
    },
    xaxis: {
      categories: xAxis,
    },
    title: {
      text: title,
      style: {
        fontSize: "14px",
        fontWeight: "bold",
        fontFamily: "Bukra",
        color: "black",
      },
    },
    plotOptions: {
      bar: {
        columnWidth: 50,
        colors: {
          ranges: [
            {
              from: -100,
              to: 0,
              color: "#b70849",
            },
            {
              from: 0,
              to: 100,
              color: "#1034a6",
            },
          ],
        },
      },
    },
    // dataLabels: {
    //   enabled: true,
    //   formatter: function (val, opts) {
    //     const isPositive = val >= 0;
    //     return isPositive && type == "progress" ? `+${val}` : val.toString();
    //   },
    // },
    yaxis: {
      forceNiceScale: true,
      labels: {
        formatter: function (val) {
          return Math.round(val);
        },
      },
    },
  });

  useEffect(() => {
    if (data) {
      setdataSeries([
        {
          name: name,
          data: data,
        },
      ]);
    }
  }, [data]);

  useEffect(() => {
    //setdataSeries(data);
    //if (data) {
    //const categories = data.map((item) => item);
    setOption((prevState) => ({
      ...prevState,
      title: {
        text: title,
      },
      xaxis: {
        categories: xAxis,
      },
    }));

    //
    //}
  }, [xAxis]);

  function eachBarClick(event, chartContext, config, value, popupX, popupY) {
    console.log(value, subTitle);
    const response = getHttp("employeeByEntityAndSubcat", {
      entity: value,
      subcat: subTitle,
    })
      .then((response) => {
        console.log(response);
        setPopupData(response);
        setPopupPosition({ popupX: popupX, popupY: popupY });
      })
      .catch((error) => {
        console.log(error);
        // handle error
      });
  }

  return (
    <div className="bar-chart">
      <Chart
        options={option}
        series={dataSeries}
        type="bar"
        height={350}
        width={width ? 550 : "100%"}
      />
      {popupData && (
        <div
          className="popup"
          style={{ left: popupPosition.popupX, top: popupPosition.popupY }}
        >
          {popupData.map((data, index) => (
            <>
              <p>
                {data.name}- {data.job}
              </p>
            </>
          ))}
          <div className="popup-arrow"></div>
        </div>
      )}
    </div>
  );
};
