import React, { useState } from "react";
import "./MultiStepForm.css";

export const MultiStepForm = () => {
  const [step, setStep] = useState(1);

  const handleNext = () => {
    setStep(step + 1);
  };

  const handlePrev = () => {
    setStep(step - 1);
  };

  const handleStep = (stepNumber) => {
    setStep(stepNumber);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your form submission logic here
    console.log("Form submitted!");
    // Reset form or navigate to the next page
  };

  // ... (other parts of your component)

  <form onSubmit={handleSubmit} className="form">
    <div className="step" style={{ display: step === 1 ? "block" : "none" }}>
      <h3>Step 1: Personal Information</h3>
      {/* Your form fields for Step 1 */}
      <button type="button" onClick={handleNext}>
        Next
      </button>
    </div>

    <div className="step" style={{ display: step === 2 ? "block" : "none" }}>
      <h3>Step 2: Contact Information</h3>
      {/* Your form fields for Step 2 */}
      <button type="button" onClick={handlePrev}>
        Previous
      </button>
      <button type="button" onClick={handleNext}>
        Next
      </button>
    </div>

    <div className="step" style={{ display: step === 3 ? "block" : "none" }}>
      <h3>Step 3: Confirmation</h3>
      {/* Your form fields for Step 3 */}
      <button type="button" onClick={handlePrev}>
        Previous
      </button>
      <button type="submit">Submit</button>
    </div>
  </form>;

  // ... (other parts of your component)

  return (
    <div className="multi-step-form-container">
      <div className="header">
        <h2>Multi-Step Form</h2>
        <div className="step-indicator">
          <span
            className={step === 1 ? "active" : ""}
            onClick={() => handleStep(1)}
          >
            1
          </span>
          <span
            className={step === 2 ? "active" : ""}
            onClick={() => handleStep(2)}
          >
            2
          </span>
          <span
            className={step === 3 ? "active" : ""}
            onClick={() => handleStep(3)}
          >
            3
          </span>
        </div>
      </div>

      <form onSubmit={handleSubmit} className="form">
        {step === 1 && (
          <div className="step">
            <h3>Step 1: Personal Information</h3>
            {/* Your form fields for Step 1 */}
            <button type="button" onClick={handleNext}>
              Next
            </button>
          </div>
        )}

        {step === 2 && (
          <div className="step">
            <h3>Step 2: Contact Information</h3>
            {/* Your form fields for Step 2 */}
            <button type="button" onClick={handlePrev}>
              Previous
            </button>
            <button type="button" onClick={handleNext}>
              Next
            </button>
          </div>
        )}

        {step === 3 && (
          <div className="step">
            <h3>Step 3: Confirmation</h3>
            {/* Your form fields for Step 3 */}
            <button type="button" onClick={handlePrev}>
              Previous
            </button>
            <button type="submit">Submit</button>
          </div>
        )}
      </form>
    </div>
  );
};
