import React, { useEffect, useRef, useState } from "react";
import Chart from "react-apexcharts";
import { getHttp } from "../../../service/APIRequest";
import { Popover } from "../../popup/Popover";
import "../Chart.css";
import "./BarChartPopup.css";

interface BarChartInterface {
  data;
  xAxis;
  title?;
  name?;
  width?: any;
  eachBarClicked?: any;
  barChartPopoverdata?: any;
  subTitle?: String;
}

export const BarChart: React.FC<BarChartInterface> = ({
  data,
  xAxis,
  title,
  name,
  width,
  eachBarClicked,
  barChartPopoverdata,
  subTitle,
}) => {
  console.log(barChartPopoverdata);
  const [dataSeries, setdataSeries] = useState<any>([
    {
      name: title, //will be displayed on the y-axis
      data: data,
    },
  ]);

  const [popupPosition, setPopupPosition] = useState(null);
  const barRef = useRef(null);
  const [popupData, setPopupData] = useState(barChartPopoverdata);
  const [option, setOption] = useState<any>({
    legend: {
      show: false,
    },
    chart: {
      events: {
        dataPointSelection: function (event, chartContext, config, subTitle) {
          const dataPointIndex = config.dataPointIndex;

          const selectedXValue = option.xaxis.categories[dataPointIndex];
          // const barElement = barRef.current;

          // const rect = barElement.getBoundingClientRect();
          // const popupX = rect.left + rect.width;
          // const popupY = rect.top + rect.height / 2;
          eachBarClick(selectedXValue, event);
        },
      },
    },
    xaxis: {
      categories: xAxis,
    },
    title: {
      text: title,
      style: {
        fontSize: "14px",
        fontWeight: "bold",
        fontFamily: "Bukra",
        color: "black",
      },
    },
    plotOptions: {
      bar: {
        columnWidth: 50,
        colors: {
          ranges: [
            {
              from: -100,
              to: 0,
              color: "#b70849",
            },
            {
              from: 0,
              to: 100,
              color: "#1034a6",
            },
          ],
        },
      },
    },
    // dataLabels: {
    //   enabled: true,
    //   formatter: function (val, opts) {
    //     const isPositive = val >= 0;
    //     return isPositive && type == "progress" ? `+${val}` : val.toString();
    //   },
    // },
    yaxis: {
      forceNiceScale: true,
      labels: {
        formatter: function (val) {
          return Math.round(val);
        },
      },
    },
  });

  useEffect(() => {
    setPopupData(barChartPopoverdata);
    //setPopupPosition(subTitle);
    if (data) {
      setdataSeries([
        {
          name: name,
          data: data,
        },
      ]);
    }
  }, [data]);

  useEffect(() => {
    //setPopupPosition(subTitle);
  }, []);

  useEffect(() => {
    //setdataSeries(data);
    //if (data) {
    //const categories = data.map((item) => item);
    //setPopupPosition(subTitle);
    setPopupData(barChartPopoverdata);
    setOption((prevState) => ({
      ...prevState,
      title: {
        text: title,
      },
      xaxis: {
        categories: xAxis,
      },
    }));
  }, [xAxis]);

  const xLabelToIndexMap = {};
  option.xaxis.categories.forEach((label, index) => {
    xLabelToIndexMap[label] = index;
  });

  const eachBarClick = (value, event) => {
    const barPosition = {
      x: event.clientX - 125,
      y: 200, //event.clientY,
    };
    eachBarClicked(value, subTitle);
    setPopupPosition(barPosition);
  };

  const wrapXAxisLabels = (chartContext, { axis, tick, text }) => {
    if (axis === "xaxis") {
      tick.classList.add("apexchart-xaxis-label");
    }
  };

  const handleClosePopover = () => {
    setPopupData(null); // Clear the popover data to close it
  };

  return (
    <div className="bar-chart" onClick={() => setPopupData(null)}>
      <Chart
        options={option}
        series={dataSeries}
        type="bar"
        height={350}
        width={width ? 550 : "100%"}
        events={{
          dataPointSelection: wrapXAxisLabels, // Apply the class to x-axis labels
        }}
      />
      {barChartPopoverdata && (
        <Popover
          tablePopupData={barChartPopoverdata}
          popoverPosition={popupPosition}
          onClose={handleClosePopover}
        />
      )}
      <div ref={barRef} style={{ display: "none" }}></div>
    </div>
  );
};
