import React, { useEffect, useState } from "react";

import Chart from "react-apexcharts";
import { BarChart } from "../../../components/Graph/barChart/BarChart";
import LineChart from "../../../components/Graph/lineChart/LineChart";
import { getHttp } from "../../../service/APIRequest";

interface Chart3Props {
  data: any;
  startDate: any;
  endDate: any;
}

export const Chart3: React.FC<Chart3Props> = ({ data, startDate, endDate }) => {
  const [chartData, setchartData] = useState(data);
  const [xAxis, setxAxis] = useState([]);
  console.log(data, chartData);
  //const [series, setSeries] = useState<any>();
  const [dateRangeProps, setdateRangeProps] = useState({
    fromDate: startDate,
    toDate: endDate,
  });

  useEffect(() => {
    getScoreOfSelectedEntity(startDate, endDate);
  }, [startDate, endDate]);

  const [series, setSeries] = useState([
    {
      name: "Improvement over last year", //chartData?.map((company) => company.entityName),
      data: chartData?.map((company) => company.avg_score_diff),
    },
  ]);

  const [options, setOptions] = useState({
    chart: {
      id: "bar-chart",
    },
    xaxis: {
      categories: chartData?.map((company) => company.entityName),
    },
    plotOptions: {
      bar: {
        colors: {
          ranges: [
            {
              from: -100,
              to: 0,
              color: "#b70849",
            },
            {
              from: 0,
              to: 100,
              color: "#1034a6",
            },
          ],
        },
      },
    },
    dataLabels: {
      enabled: true,
      formatter: function (val, opts) {
        const isPositive = val >= 0;
        return isPositive ? `+${val}` : val.toString();
      },
    },
  });

  async function drawChart(responseData?) {
    console.log("==------====", responseData);

    var dataOfChart = {
      name: "Improvement over last year", //chartData?.map((company) => company.entityName),
      data: responseData?.map((company) => company.avg_score_diff),
    };
    //   name: responseData[0].companyName,
    //   data: responseData[0].monthlyAverages.map((monthlyAverage) => {
    //     return monthlyAverage.averageScore;
    //   }),
    // };
    var xAxisdata = chartData?.map((company) => company.entityName);
    setxAxis(xAxisdata);
    setchartData(dataOfChart);
    // setchartData((prevState) => [...prevState, dataOfChart]);
  }

  const getScoreOfSelectedEntity = async (startDate, endDate) => {
    const response = await getHttp("skills/skill-ranks", {
      startDate: dateRangeProps.fromDate || startDate,
      endDate: dateRangeProps.toDate || endDate,
    });
    drawChart(response);
  };

  //   useEffect(() => {
  //     setOptions((prevState) => ({
  //       ...prevState,
  //       xaxis: {
  //         ...prevState.xaxis,
  //         categories: data?.map((company) => company.entityName),
  //       },
  //     }));
  //   }, [startDate, data]);

  return (
    <div>
      {/* <Chart options={options} series={series} type="bar" height={350} /> */}
      <BarChart data={chartData} xAxis={xAxis} />
    </div>
  );
};
