import React, { useEffect, useState } from "react";
import Chart from "react-apexcharts";
import "../Chart.css";

interface radialChartInterface {
  details?: any;
  heading: String;
}

const RadialBarChart: React.FC<radialChartInterface> = ({
  details,
  heading,
}) => {
  // const [display, setDisplay] = useState(false);
  const data = {
    options: {
      labels: details?.map((item) => item.category),
      legend: {
        show: true,
        showForSingleSeries: false,
        showForNullSeries: true,
        showForZeroSeries: true,
        //position: "top",
      },
      responsive: [
        {
          breakpoint: 480,
          options: {
            chart: {
              width: 200,
            },
          },
        },
      ],
      plotOptions: {
        radialBar: {
          hollow: {
            margin: 15,
            size: "60%",
            background: "transparent",
            image: undefined,
          },
          dataLabels: {
            name: {
              show: true,
            },
            value: {
              show: true,
            },
          },
        },
      },
    },
    series: details?.map((item) => item.count),
  };

  useEffect(() => {
    //setTimeout(() => setDisplay(true), 100);
  }, []);

  return (
    <div className="mixed-chart">
      <div className="radialHeading" hidden={details?.length <= 0}>
        {heading}
      </div>
      <Chart
        options={data.options}
        series={data.series}
        type="radialBar"
        width="500"
      />
    </div>
  );
};

export default RadialBarChart;
