import React, { useEffect, useState } from "react";
import { getHttp } from "../../service/APIRequest";
import { allSkillsByRoleId } from "../../service/Config";

export const CompetancyEvaluation = () => {
  const initialCourse = {
    course_name: "",
    enrollment_date: "",
    enrollment_result: "",
  };
  const initialSkill = {
    name: "",
    evaluation_result: "",
    evaluation_date: "",
    courses: [initialCourse],
  };

  const [employeeData, setEmployeeData] = useState({
    employee_number: "",
    name: "",
    role: "",
    organization: "",
    total_exp: "",
    cs_exp: "",
    job_profile: "",
    skills: [initialSkill],
  });

  const handleAddSkill = () => {
    setEmployeeData((prevData) => ({
      ...prevData,
      skills: [...prevData.skills, { ...initialSkill }],
    }));
  };

  const handleAddCourse = (skillIndex) => {
    setEmployeeData((prevData) => ({
      ...prevData,
      skills: prevData.skills.map((skill, idx) =>
        idx === skillIndex
          ? { ...skill, courses: [...skill.courses, { ...initialCourse }] }
          : skill
      ),
    }));
  };

  const handleSkillChange = (e, skillIndex) => {
    const { name, value } = e.target;
    setEmployeeData((prevData) => ({
      ...prevData,
      skills: prevData.skills.map((skill, idx) =>
        idx === skillIndex ? { ...skill, [name]: value } : skill
      ),
    }));
  };

  const handleCourseChange = (e, skillIndex, courseIndex) => {
    const { name, value } = e.target;
    setEmployeeData((prevData) => ({
      ...prevData,
      skills: prevData.skills.map((skill, sIdx) =>
        sIdx === skillIndex
          ? {
              ...skill,
              courses: skill.courses.map((course, cIdx) =>
                cIdx === courseIndex ? { ...course, [name]: value } : course
              ),
            }
          : skill
      ),
    }));
  };

  const handleSave = () => {
    // This is where you can use the 'employeeData' state to send it to the backend
    console.log(employeeData);
  };

  return (
    <div>
      {employeeData.skills.map((skill, skillIndex) => (
        <div key={skillIndex}>
          <input
            type="text"
            name="name"
            value={skill.name}
            onChange={(e) => handleSkillChange(e, skillIndex)}
            placeholder="Skill Name"
          />
          <input
            type="text"
            name="evaluation_result"
            value={skill.evaluation_result}
            onChange={(e) => handleSkillChange(e, skillIndex)}
            placeholder="Evaluation Result"
          />
          {/* Add more input fields for evaluation date and other skill details */}

          {skill.courses.map((course, courseIndex) => (
            <div key={courseIndex}>
              <input
                type="text"
                value={course.course_name}
                onChange={(e) => handleCourseChange(e, skillIndex, courseIndex)}
                placeholder="Course Name"
              />
              {/* Add input fields for enrollment date, enrollment result, etc. */}

              {courseIndex === skill.courses.length - 1 && (
                <button onClick={() => handleAddCourse(skillIndex)}>+</button>
              )}
            </div>
          ))}
          <button onClick={handleAddSkill}>Add Skill</button>
        </div>
      ))}
      <button onClick={handleSave}>Save</button>
    </div>
  );
};
