import React, { useEffect, useRef, useState } from "react";
import Chart from "react-apexcharts";
import { getHttp } from "../../../service/APIRequest";
import { Popover } from "../../popup/Popover";
import "../Chart.css";
import "./BarChartPopup.css";

interface BarChartInterface {
  data;
  xAxis;
  title;
  name?;
  width?: any;
  eachBarClicked?: any;
  barChartPopoverdata?: any;
}

export const BarChart: React.FC<BarChartInterface> = ({
  data,
  xAxis,
  title,
  name,
  width,
  eachBarClicked,
  barChartPopoverdata,
}) => {
  const [dataSeries, setdataSeries] = useState<any>([
    {
      name: title, //will be displayed on the y-axis
      data: data,
    },
  ]);

  const [popupPosition, setPopupPosition] = useState(null);
  const barRef = useRef(null);
  const [popupData, setPopupData] = useState(barChartPopoverdata);
  const [option, setOption] = useState<any>({
    legend: {
      show: false,
    },
    chart: {
      events: {
        dataPointSelection: function (event, chartContext, config) {
          const dataPointIndex = config.dataPointIndex;

          const selectedXValue = option.xaxis.categories[dataPointIndex];
          const barElement = barRef.current;

          const rect = barElement.getBoundingClientRect();
          const popupX = rect.left + rect.width;
          const popupY = rect.top + rect.height / 2;
          eachBarClick(
            event,
            chartContext,
            config,
            selectedXValue,
            popupX,
            popupY
          );
        },
      },
    },
    xaxis: {
      categories: xAxis,
    },
    title: {
      text: title,
      style: {
        fontSize: "14px",
        fontWeight: "bold",
        fontFamily: "Bukra",
        color: "black",
      },
    },
    plotOptions: {
      bar: {
        columnWidth: 50,
        colors: {
          ranges: [
            {
              from: -100,
              to: 0,
              color: "#b70849",
            },
            {
              from: 0,
              to: 100,
              color: "#1034a6",
            },
          ],
        },
      },
    },
    // dataLabels: {
    //   enabled: true,
    //   formatter: function (val, opts) {
    //     const isPositive = val >= 0;
    //     return isPositive && type == "progress" ? `+${val}` : val.toString();
    //   },
    // },
    yaxis: {
      forceNiceScale: true,
      labels: {
        formatter: function (val) {
          return Math.round(val);
        },
      },
    },
  });

  useEffect(() => {
    if (data) {
      setdataSeries([
        {
          name: name,
          data: data,
        },
      ]);
    }
  }, [data]);

  useEffect(() => {
    //setdataSeries(data);
    //if (data) {
    //const categories = data.map((item) => item);
    setOption((prevState) => ({
      ...prevState,
      title: {
        text: title,
      },
      xaxis: {
        categories: xAxis,
      },
    }));

    //
    //}
  }, [xAxis]);

  function eachBarClick(event, chartContext, config, value, popupX, popupY) {
    // const response = getHttp("employeeByEntityAndSubcat", {
    //   entity: value,
    //   subcat: popupSubtitle,
    // })
    //   .then((response) => {
    //     setPopupData(response);
    //     setPopupPosition({ popupX: popupX, popupY: popupY });
    //   })
    //   .catch((error) => {
    //     console.log(error);
    //     // handle error
    //   });
    eachBarClicked(value);
  }

  return (
    <div className="bar-chart" onClick={() => setPopupData(null)}>
      <Chart
        options={option}
        series={dataSeries}
        type="bar"
        height={350}
        width={width ? 550 : "100%"}
      />
      {popupData && (
        // <div
        //   className="popup"
        //   style={{ left: popupPosition.popupX, top: popupPosition.popupY }}
        // >
        //   {popupData.map((data, index) => (
        //     <p key={index}>
        //       {data.name}- {data.job}
        //     </p>
        //   ))}
        //   <div className="popup-arrow"></div>
        // </div>
        <Popover tablePopupData={popupData} />
      )}
      <div ref={barRef} style={{ display: "none" }}></div>
    </div>
  );
};
