import React, { useEffect, useState } from "react";
import Chart from "react-apexcharts";

export const BarChart = ({ data, xAxis }) => {
  const [dataSeries, setdataSeries] = useState(data || []);
  const [option, setOption] = useState<any>({
    legend: {
      show: false,
    },
    xaxis: {
      categories: xAxis,
    },
    plotOptions: {
      bar: {
        colors: {
          ranges: [
            {
              from: -100,
              to: 0,
              color: "#b70849",
            },
            {
              from: 0,
              to: 100,
              color: "#1034a6",
            },
          ],
        },
      },
    },
    yaxis: {
      forceNiceScale: true,
    },
  });

  useEffect(() => {
    setdataSeries(data);

    if (data) {
      const categories = data.map((item) => item.category);
      setOption((prevState) => ({
        ...prevState,
        xaxis: {
          ...prevState.xaxis,
          categories: xAxis,
        },
      }));
    }
  }, [data]);
  return (
    <div>
      {" "}
      <Chart options={option} series={dataSeries} type="bar" height={350} />
    </div>
  );
};
