import React, { useEffect, useState } from "react";
import { getHttp } from "../../service/APIRequest";
import "./DropDown.css";
import Select from "react-select";

interface dropDownType {
  onChangeDropDownItem?: any;
  mainList?: any;
  name?: String;
}
const DropDown: React.FC<dropDownType> = ({
  onChangeDropDownItem,
  mainList,
  name,
}) => {
  const [selectedList, setSelectedList] = useState("employeeRole");
  //const [selectedModel, setSelectedModel] = useState("");
  const [displayDataSet, setdisplayDataSet] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState();
  const [selectedOption, setSelectedOption] = useState(null);

  useEffect(() => {
    // fetchData();
    console.log("eeee", mainList);
  }, [selectedOptions]);

  async function fetchData(param?) {
    if (onChangeDropDownItem) {
      const response = await getHttp("getAll/" + selectedList);
      setdisplayDataSet(response);
      onChangeDropDownItem && onChangeDropDownItem(response || []);
    }
  }
  const options = [
    { value: "chocolate", label: "Chocolate" },
    { value: "strawberry", label: "Strawberry" },
    { value: "vanilla", label: "Vanilla" },
  ];

  function handleBrandChange(e) {
    setSelectedList(e.target.value);
  }

  // function handleModelChange(e) {
  //   setSelectedModel(e.target.value);
  // }

  // Function triggered on selection
  function handleSelect(data) {
    setSelectedOptions(data);
    onChangeDropDownItem(data);
    console.log(data);
  }

  return (
    <div>
      <div className="app">
        <div>Choose {name}</div>
        <div className="dropdown-container">
          <Select
            options={mainList}
            isMulti
            placeholder={`Select ${name}`}
            onChange={handleSelect}
            isSearchable={true}
          />
        </div>
        <Select
          defaultValue={selectedOption}
          onChange={setSelectedOption}
          options={options}
        />
      </div>
    </div>
  );
};
export default DropDown;
