import React, { useEffect, useState } from "react";
import DateInput from "../datePicker/DateInput";

export const DateRangeFilter = () => {
  const [startDate, setStartDate] = useState<any>();
  const [endDate, setEndDate] = useState(null);
  const [endCalendarVisible, setEndCalendarVisible] = useState(false);

  useEffect(() => {
    const today = new Date();
    const defaultStartDate = today.toISOString().slice(0, 10);
    setStartDate(defaultStartDate);
  }, []);

  const handleStartChange = (date) => {
    setStartDate(date);
    setEndCalendarVisible(true);
  };

  const handleEndChange = (date) => {
    setEndDate(date);
  };

  //   const filterData = () => {
  //     console.log(startDate, endDate);
  //     if (startDate && endDate) {
  //       const filteredData = data.filter(
  //         (item) =>
  //           new Date(item.date) >= new Date(startDate) &&
  //           new Date(item.date) <= new Date(endDate)
  //       );
  //       console.log(filteredData);
  //     }
  //   };

  const filterData = () => {
    if (startDate && endDate) {
      console.log(startDate, "----------", endDate);
      return { startDate, endDate };
    }
    return null;
  };

  function changeFilterStartDate(changeInStartDate) {
    setEndCalendarVisible(true);
    setStartDate("2023-04-03");
    setEndDate(changeInStartDate);
    console.log(changeInStartDate);
  }

  function changeFilterEndDate(enddate) {
    console.log(enddate);
  }

  return (
    <div>
      <div>
        <label>Start Date:</label>
        <DateInput
          maxDate={new Date().toISOString().substr(0, 10)}
          value={"2023-04-04"}
          dateChangeEvent={changeFilterStartDate}
        />
      </div>
      {endCalendarVisible && (
        <div>
          <label>End Date:</label>
          <DateInput
            maxDate={startDate}
            value={startDate}
            dateChangeEvent={changeFilterEndDate}
          />
        </div>
      )}
      <button onClick={filterData}>Filter Data</button>
    </div>
  );
};
