import React from "react";
import "./Popover.css";

interface PopoverInterface {
  tablePopupData: any;
}

export const Popover = ({ tablePopupData }) => {
  return (
    <div
      className="popup"
      // style={{
      //   left: popupPosition.popupX,
      //   top: popupPosition.popupY,
      // }}
    >
      {tablePopupData.map((data, index) => (
        <p key={index}>
          {data.name}- {data.job}
        </p>
      ))}
      <div className="popup-arrow"></div>
    </div>
  );
};
