                  <tr className="table-heading">
                    <th className="each-column-border ">Rank </th>
                  </tr>
                </thead>
              )}
              {index >= 5 && (
                <tr key={index}>
                  <td className="each-column-border">{item.index + 1}</td>
                  <td className="each-column-border">{item.companyName}</td>
                </tr>
              )}
            </>
