import React from "react";

import Chart from "react-apexcharts";

const LineChart = ({ data }) => {
  //   const series = [
  //     { name: "Estimate", data: [10, 15, 2] },
  //     { name: "Actual", data: [12, 10, 20] },
  //   ];
  const series = data;
  const options = {
    xaxis: {
      categories: [
        "2023-05-01",
        "2022-05-02",
        "20121-05-03",
        "2023-05-01",
        "2022-05-02",
        "20121-05-03",
      ],
    },
  };

  return (
    <div>
      <Chart options={options} series={series} type="line" width={400} />
    </div>
  );
};

export default LineChart;
