  //   const formData = new FormData();
  //   formData.append("file", file);

  //   postHttp("upload/" + selectedFileType, formData)
  //     .then((response) => {
  //       console.log(response);
  //       setresponseText(response);
  //       // handle success
  //     })
  //     .catch((error) => {
  //       console.log(error);
  //       setresponseText(error);
  //       // handle error
  //     });
  // };

  // const handleSubmit = (e) => {
