  //   handleFileUpload();
  //   // Clear the file input
  //   e.target.value = null;
  // };

  var typeOfDropdown = [
    { id: 1, label: "Employee", value: "Employee" },
    { id: 2, label: "Training", value: "Training" },
    { id: 3, label: "Entity", value: "Entity" },
  ];
  return (
    <div className="upload-backgroound">
      <div className="upload-container">
        {/* <form
         onSubmit={() => handleSubmit}
        > */}
        {/* <select value={selectedFileType} onChange={handleItemSelect}>
            <option value="">Select File Type</option>
            <option value="employee">Employee</option>
            <option value="training">Training</option>
            <option value="entity">Entity</option>
          </select> */}
        <div>
          <DropDown
            mainList={typeOfDropdown}
            name=""
            onChangeDropDownItem={handleFileChange}
            isMulti={false}
            classname="margin"
            placeholder={"Select file type"}
          />
          <input
            type="file"
            onChange={handleFileChange}
            onClick={handleResponseText}
          />
          <button type="submit" onClick={() => handleSubmit}>
            Upload
          </button>
        </div>
        {/* </form> */}
      </div>
      <div className="response-text">{responseText}</div>
    </div>
  );
};

export default FileUpload;
