import React, { useEffect, useState } from "react";
import { getHttp } from "../../service/APIRequest";
import { allSkillsByRoleId } from "../../service/Config";

export const CompetancyEvaluation = () => {
  const [technicalSkills, setTechnicalSkills] = useState([
    {
      id: 1,
      name: "Skill 1",
      trainingDetails: [{ details: "", enrollmentDate: "", status: "" }],
      status: "",
      enrollmentDate: "",
    },
    {
      id: 2,
      name: "Skill 2",
      trainingDetails: [{ details: "", enrollmentDate: "", status: "" }],
      status: "",
      enrollmentDate: "",
    },
    // Add more technical skills as needed
  ]);

  const [softSkills, setSoftSkills] = useState([
    {
      id: 1,
      name: "Skill A",
      trainingDetails: [{ details: "", enrollmentDate: "", status: "" }],
      status: "",
      enrollmentDate: "",
    },
    {
      id: 2,
      name: "Skill B",
      trainingDetails: [{ details: "", enrollmentDate: "", status: "" }],
      status: "",
      enrollmentDate: "",
    },
    // Add more soft skills as needed
  ]);

  const [allSkills, setallSkills] = useState<any>({});

  useEffect(() => {
    getSkillSet();
  }, []);

  const getSkillSet = () => {
    getHttp(allSkillsByRoleId + 4)
      .then((response) => {
        console.log(response);
        setallSkills(response.data);
      })
      .catch((error) => {
        console.log(error);
        // handle error
      });
    ///
  };

  const handleTrainingDetailsChange = (
    category,
    skillId,
    detailIndex,
    field,
    value
  ) => {
    if (category === "technical") {
      const updatedSkills = technicalSkills.map((skill) =>
        skill.id === skillId
          ? {
              ...skill,
              trainingDetails: skill.trainingDetails.map((detail, index) =>
                index === detailIndex ? { ...detail, [field]: value } : detail
              ),
            }
          : skill
      );
      setTechnicalSkills(updatedSkills);
    } else if (category === "soft") {
      const updatedSkills = softSkills.map((skill) =>
        skill.id === skillId
          ? {
              ...skill,
              trainingDetails: skill.trainingDetails.map((detail, index) =>
                index === detailIndex ? { ...detail, [field]: value } : detail
              ),
            }
          : skill
      );
      setSoftSkills(updatedSkills);
    }
  };

  const handleAddTrainingDetails = (category, skillId) => {
    if (category === "technical") {
      const updatedSkills = technicalSkills.map((skill) =>
        skill.id === skillId
          ? {
              ...skill,
              trainingDetails: [
                ...skill.trainingDetails,
                { details: "", enrollmentDate: "", status: "" },
              ],
            }
          : skill
      );
      setTechnicalSkills(updatedSkills);
    } else if (category === "soft") {
      const updatedSkills = softSkills.map((skill) =>
        skill.id === skillId
          ? {
              ...skill,
              trainingDetails: [
                ...skill.trainingDetails,
                { details: "", enrollmentDate: "", status: "" },
              ],
            }
          : skill
      );
      setSoftSkills(updatedSkills);
    }
  };

  return (
    <div>
      <h2>Technical Skills</h2>
      {allSkills.technicalSkills.map((skill) => (
        <div key={skill.id}>
          <input type="checkbox" onChange={() => {}} />
          <label>{skill.name}</label>
          {skill.trainingDetails.map((detail, index) => (
            <div key={index}>
              <input
                type="text"
                value={detail.details}
                onChange={(e) =>
                  handleTrainingDetailsChange(
                    "technical",
                    skill.id,
                    index,
                    "details",
                    e.target.value
                  )
                }
                placeholder="Training Details"
              />
              <input
                type="text"
                value={detail.enrollmentDate}
                onChange={(e) =>
                  handleTrainingDetailsChange(
                    "technical",
                    skill.id,
                    index,
                    "enrollmentDate",
                    e.target.value
                  )
                }
                placeholder="Enrollment Date"
              />
              <input
                type="text"
                value={detail.status}
                onChange={(e) =>
                  handleTrainingDetailsChange(
                    "technical",
                    skill.id,
                    index,
                    "status",
                    e.target.value
                  )
                }
                placeholder="Status"
              />
            </div>
          ))}
          <button
            onClick={() => handleAddTrainingDetails("technical", skill.id)}
          >
            Add More Training Details
          </button>
        </div>
      ))}

      {/* Soft Skills section */}
      {/* Similar structure as Technical Skills */}
    </div>
  );
};
