import React, { useState } from "react";

export const CompetancyEvaluation = () => {
  const [technicalSkills, setTechnicalSkills] = useState([
    {
      id: 1,
      name: "Skill 1",
      trainingDetails: "",
      status: "",
      enrollmentDate: "",
    },
    {
      id: 2,
      name: "Skill 2",
      trainingDetails: "",
      status: "",
      enrollmentDate: "",
    },
    // Add more technical skills as needed
  ]);

  const [softSkills, setSoftSkills] = useState([
    {
      id: 1,
      name: "Skill A",
      trainingDetails: "",
      status: "",
      enrollmentDate: "",
    },
    {
      id: 2,
      name: "Skill B",
      trainingDetails: "",
      status: "",
      enrollmentDate: "",
    },
    // Add more soft skills as needed
  ]);

  const handleTrainingDetailsChange = (category, id, value) => {
    if (category === "technical") {
      const updatedSkills = technicalSkills.map((skill) =>
        skill.id === id ? { ...skill, trainingDetails: value } : skill
      );
      setTechnicalSkills(updatedSkills);
    } else if (category === "soft") {
      const updatedSkills = softSkills.map((skill) =>
        skill.id === id ? { ...skill, trainingDetails: value } : skill
      );
      setSoftSkills(updatedSkills);
    }
  };

  // Add more functions to handle status, enrollment date changes, etc.

  return (
    <div>
      <h2>Technical Skills</h2>
      {technicalSkills.map((skill) => (
        <div key={skill.id}>
          <label>{skill.name}</label>

          <input
            type="checkbox"
            onChange={() => {}}
            // Add checkbox logic
          />
          <input
            type="text"
            value={skill.trainingDetails}
            onChange={(e) =>
              handleTrainingDetailsChange("technical", skill.id, e.target.value)
            }
            placeholder="Training Details"
          />
          {/* Add more input fields for enrollment date, status, etc. */}
        </div>
      ))}
      <button onClick={() => {}}>Add More Technical Skills</button>

      <h2>Soft Skills</h2>
      {softSkills.map((skill) => (
        <div key={skill.id}>
          <input
            type="checkbox"
            onChange={() => {}}
            // Add checkbox logic
          />
          <label>{skill.name}</label>
          <input
            type="text"
            value={skill.trainingDetails}
            onChange={(e) =>
              handleTrainingDetailsChange("soft", skill.id, e.target.value)
            }
            placeholder="Training Details"
          />
          {/* Add more input fields for enrollment date, status, etc. */}
        </div>
      ))}
      <button onClick={() => {}}>Add More Soft Skills</button>
    </div>
  );
};
