import React, { useEffect, useState } from "react";
import { getHttp } from "../../service/APIRequest";
import {
  excelMainList,
  mainCategory,
  mainEntity,
} from "../../service/Constants";
import DropDown from "../dropDown/DropDown";
import "./FilterSection.css";

interface filterSectionProps {
  onSelectedValueChange: any;
  allEntities;
}

const FilterSection: React.FC<filterSectionProps> = ({
  onSelectedValueChange,
  allEntities,
}) => {
  const [mainCategoryList, setMainCategoryList] = useState([]);
  const [entityList, setentityList] = useState(allEntities);

  useEffect(() => {
    fetchCatagoryList();
  }, []);

  async function fetchCatagoryList(param?) {
    // if (onChangeDropDownItem) {
    const response = await getHttp("main-categories");
    setMainCategoryList(response);
    //onChangeDropDownItem && onChangeDropDownItem(response || []);
    //}
  }

  const handleChangeValue = (data) => {
    if (data.length === 0) {
      onSelectedValueChange(allEntities);
    } else {
      onSelectedValueChange(data);
    }
  };

  return (
    <div className="all-filter-container">
      <DropDown
        mainList={entityList}
        name="Entity"
        onChangeDropDownItem={handleChangeValue}
      />
    </div>
  );
};

export default FilterSection;
