import axios from "axios";
import React, { useState } from "react";
import { postHttp } from "../../service/APIRequest";
import "./FileUpload.css";

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [responseText, setresponseText] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [selectedFileType, setSelectedFileType] = useState("");

  const handleFileSelect = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleFileTypeSelect = (event) => {
    setSelectedFileType(event.target.value);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleResponseText = () => {
    setresponseText(null);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("file", file);

    const response = postHttp("upload", formData)
      .then((response) => {
        console.log(response);
        setresponseText(response);
        // handle success
      })
      .catch((error) => {
        console.log(error);
        setresponseText(error);
        // handle error
      });

    // axios
    //   .post("http://localhost:8080/upload", formData, {
    //     headers: {
    //       "Content-Type": "multipart/form-data",
    //     },
    //   })
    //   .then((response) => {
    //     console.log(response);
    //     setresponseText(response.data);
    //     // handle success
    //   })
    //   .catch((error) => {
    //     console.log(error);
    //     // handle error
    //   });
  };
  return (
    <div className="upload-backgroound">
      <div className="upload-container">
        <form onSubmit={handleSubmit}>
          <select value={selectedFileType} onChange={handleFileTypeSelect}>
            <option value="">Select File Type</option>
            <option value="type1">Employee</option>
            <option value="type2">Training</option>
            <option value="type3">Entity</option>
            {/* Add more file types as needed */}
          </select>
          <input
            type="file"
            onChange={handleFileChange}
            onClick={handleResponseText}
          />
          <button type="submit">Upload</button>
        </form>
      </div>
      <div className="response-text">{responseText}</div>
    </div>
  );
};

export default FileUpload;
