import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const DateInput = () => {
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(null);
  const [endCalendarVisible, setEndCalendarVisible] = useState(false);

  const handleStartChange = (date) => {
    setStartDate(date);
    setEndCalendarVisible(true);
  };

  const handleEndChange = (date) => {
    setEndDate(date);
  };

  //   const filterData = () => {
  //     console.log(startDate, endDate);
  //     if (startDate && endDate) {
  //       const filteredData = data.filter(
  //         (item) =>
  //           new Date(item.date) >= new Date(startDate) &&
  //           new Date(item.date) <= new Date(endDate)
  //       );
  //       console.log(filteredData);
  //     }
  //   };

  const filterData = () => {
    if (startDate && endDate) {
      console.log(startDate, "----------", endDate);
      return { startDate, endDate };
    }
    return null;
  };

  const data = [
    { id: 1, date: "2022-12-01" },
    { id: 2, date: "2023-02-01" },
    { id: 3, date: "2023-03-01" },
    { id: 4, date: "2023-04-01" },
    { id: 5, date: "2023-01-01" },
  ];

  const monthNames = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  const renderCustomHeader = ({ date, decreaseMonth, increaseMonth }) => {
    const month = monthNames[date.getMonth()];
    const year = date.getFullYear();
    return (
      <div className="header">
        <button className="prev" onClick={decreaseMonth}>
          {"<"}
        </button>
        <div className="current">
          {month} {year}
        </div>
        <button className="next" onClick={increaseMonth}>
          {">"}
        </button>
      </div>
    );
  };
  const today = new Date();
  const currentMonth = today.getMonth();
  const currentYear = today.getFullYear();

  const disabledMonths = Array.from(
    { length: startDate.getMonth() },
    (_, i) => new Date(startDate.getFullYear(), i, 1)
  );

  return (
    <div>
      <div>
        <label>Start Date:</label>
        <DatePicker
          selected={startDate}
          onChange={handleStartChange}
          showMonthYearPicker
          renderCustomHeader={renderCustomHeader}
          maxDate={new Date(currentYear, currentMonth)}
        />
      </div>
      {endCalendarVisible && (
        <div>
          <label>End Date:</label>
          <DatePicker
            selected={endDate}
            onChange={handleEndChange}
            showMonthYearPicker
            renderCustomHeader={renderCustomHeader}
            minDate={startDate}
            disabledMonths={disabledMonths}
            maxDate={new Date(currentYear, currentMonth)}
          />
        </div>
      )}
      <button onClick={filterData}>Filter Data</button>
    </div>
  );
};

export default DateInput;
